mapper.select:
/* selectBannerList */ SELECT banner_no bannerNo , banner_img_url bannerImgUrl , banner_tp bannerTypeCd, CASE WHEN banner_tp = 'BA01' THEN (SELECT tt.trans_en FROM NOTICE_TB nt INNER JOIN TRANSLATION_TB tt ON nt.notice_title_t_no = tt.trans_no WHERE nt.notice_no = bt.target) WHEN banner_tp = 'BA02' THEN (SELECT tt.trans_en FROM OPERATED_CHALLENGE_TB oct INNER JOIN BASE_CHALLENGE_TB bct ON oct.challenge_base_no = bct.base_no INNER JOIN TRANSLATION_TB tt ON bct.base_nm_t_no = tt.trans_no WHERE oct.challenge_no = bt.target) ELSE target END bannerTargetNameEn , banner_is_on bannerIsOn , banner_is_main bannerIsMain FROM BANNER_TB bt ORDER BY
${sortField} ${sortOrder},
banner_is_on DESC, banner_no DESC LIMIT ${startIndex}, ${pageSize}

mapper.insert:
/* insertNewBanner */
INSERT INTO BANNER_TB (banner_img_url, banner_tp, target, banner_is_on, banner_is_main)
VALUES (#{bannerImgUrl}, #{bannerTypeCd}, #{bannerTarget}, 0, #{bannerIsMain})

mapper.update:
/* updateBannerActiveStatus */
UPDATE BANNER_TB 
SET banner_is_on = #{bannerIsOn}
WHERE banner_no = #{bannerNo};

mapper.select:
		/* selectBannerDetail */
		SELECT banner_no bannerNo, banner_img_url bannerImgUrl, banner_tp bannerTypeCd, target bannerTarget, banner_is_main bannerIsMain
 		FROM BANNER_TB 
 		WHERE banner_no = #{bannerNo}

mapper.update:
/* updateBanner */
UPDATE BANNER_TB
SET

	banner_img_url = #{bannerImgUrl},


	target = #{bannerTarget},

banner_tp = #{bannerTypeCd},
banner_is_main = #{bannerIsMain}
WHERE banner_no = #{bannerNo}

mapper.delete:
/* deleteBanner */
DELETE FROM BANNER_TB
WHERE banner_no = #{bannerNo}
