mapper.select:
/* selectCashReportList */
SELECT ucrt.cash_report_no cashReportNo, ut.user_id userId, ucrt.report_cash_amt reportCashAmount, ucrt.report_account reportAccount, ucrt.report_bank reportBank, ucrt.report_account_holder_first_nm reportAccountHolderFirstName, ucrt.report_account_holder_last_nm reportAccountHolderLastName, DATE_FORMAT(ucrt.report_reg_dt, '%Y-%m-%d %H:%i:%s') reportRegDate, ucrt.report_st_cd reportStatusCd

ORDER BY

	${sortField} ${sortOrder},

ucrt.report_reg_dt DESC
LIMIT ${startIndex}, ${pageSize}

mapper.sql:
FROM USER_CASH_REPORT_TB ucrt
INNER JOIN USER_TB ut ON ucrt.cash_user_no = ut.user_no
WHERE ucrt.report_tp_cd = 'CA03'
AND ucrt.report_st_cd != 'CT03'
AND ut.user_nationality_cd = #{nationalityCd}

	AND UPPER(ucrt.report_bank) = UPPER(#{bank})


	AND ucrt.report_st_cd = #{reportStatusCd}


	AND DATE_FORMAT(ucrt.report_reg_dt, '%Y-%m-%d') = #{reportRegDate}


	AND ut.user_id LIKE CONCAT('%', #{userId}, '%')
