mapper.select:
/* selectCategoryList */ SELECT ct.category_no categoryNo, tt.trans_en categoryNmEn, (SELECT COUNT(*) FROM OPERATED_CHALLENGE_TB WHERE challenge_category_no = ct.category_no) challengeNum, category_is_main categoryIsMain, category_order categoryOrder FROM CATEGORY_TB ct INNER JOIN TRANSLATION_TB tt ON ct.category_nm_t_no = tt.trans_no ORDER BY
${sortField} ${sortOrder},
category_order LIMIT ${startIndex}, ${pageSize}

mapper.update:
/* toggleCategoryIsMain */
UPDATE CATEGORY_TB 
SET category_is_main = !category_is_main 
WHERE category_no = #{categoryNo}

/* updateCategoryOrders */
UPDATE CATEGORY_TB
SET category_order = #{categoryOrder}
WHERE category_no = #{categoryNo}
