mapper.select:
/* getContentForPushMessage */
SELECT result_msg 
FROM RESULT_CODE_TB 
WHERE main_cd = #{mainCode} AND detail_cd = #{detailCode} AND language_cd = #{languageCode}

/* getLastLanguageForUser */
SELECT IFNULL(user_last_lang_cd, 'LN01') 
FROM USER_TB 
WHERE user_no = #{userNo}

/* getChallengeNameByLanguage */ SELECT
tt.trans_en
tt.trans_t_cn
tt.trans_s_cn
FROM BASE_CHALLENGE_TB bct INNER JOIN OPERATED_CHALLENGE_TB oct ON bct.base_no = oct.challenge_base_no INNER JOIN TRANSLATION_TB tt ON tt.trans_no = bct.base_nm_t_no WHERE oct.challenge_no = #{operatedChallengeNo}
