mapper.select:
/* selectCouponList */ SELECT coupon_no couponNo, coupon_code couponCode, coupon_tp couponType, coupon_nm couponName, coupon_gift_amt couponGiftAmount, coupon_use_num couponUseNumber, coupon_max_use couponMaxUse, coupon_expired_dt couponExpiredDate, coupon_is_on couponIsOn FROM COUPON_TB WHERE coupon_is_on = 1 ORDER BY
${sortField} ${sortOrder},
coupon_reg_dt DESC LIMIT ${startIndex}, ${pageSize}

/* selectCouponDetail */
SELECT coupon_no couponNo, coupon_code couponCode, coupon_tp couponType, coupon_nm couponName, coupon_gift_amt couponGiftAmount, coupon_use_num couponUseNumber, coupon_max_use couponMaxUse, coupon_expired_dt couponExpiredDate
FROM COUPON_TB
WHERE COUPON_NO = #{couponNo}

mapper.update:
/* updateCouponStatus */
UPDATE COUPON_TB
SET coupon_is_on = 0
WHERE coupon_no = #{couponNo}

mapper.select:
/* selectCouponUseList */
SELECT ut.user_id userId, ut.user_nickname nickName, DATE_FORMAT(cuht.c_history_reg_dt, '%Y-%m-%d %H:%i:%s') couponUseDate
FROM COUPON_USE_HISTORY_TB cuht
INNER JOIN USER_TB ut ON cuht.c_history_user_no = ut.user_no
WHERE cuht.c_history_coupon_no = #{couponNo}
ORDER BY

	${sortField} ${sortOrder},

c_history_reg_dt DESC
LIMIT ${startIndex}, ${pageSize}

/* getTotalCouponUseCount */
SELECT COUNT(*) totalCouponUseCount
FROM COUPON_USE_HISTORY_TB cuht
INNER JOIN USER_TB ut ON cuht.c_history_user_no = ut.user_no
WHERE cuht.c_history_coupon_no = #{couponNo}
