mapper.select:
/* getTotalOfferCount */
SELECT COUNT(*) totalOfferCount
FROM CHALLENGE_OFFER_TB

	WHERE offer_st_cd = #{offerStatusCd}

/* selectOfferList */ SELECT * FROM ( SELECT cot.OFFER_NO offerNo, ut.USER_ID userId, (CASE WHEN cot.offer_st_cd = 'VO01' THEN cot.offer_title ELSE tt.trans_en END) offerTitle, cot.offer_st_cd offerStatusCd, cot.offer_like_num offerLikeNum, cot.offer_reg_dt offerRegDate FROM CHALLENGE_OFFER_TB cot INNER JOIN USER_TB ut ON cot.offer_user_no = ut.user_no LEFT JOIN TRANSLATION_TB tt ON cot.OFFER_TITLE_T_NO = tt.TRANS_NO
WHERE cot.offer_st_cd = #{offerStatusCd}
UNION ALL SELECT cot.OFFER_NO offerNo, 'admin' userId, tt.trans_en offerTitle, cot.offer_st_cd offerStatusCd, cot.offer_like_num offerLikeNum, cot.offer_reg_dt offerRegDate FROM CHALLENGE_OFFER_TB cot INNER JOIN TRANSLATION_TB tt ON cot.offer_title_t_no = tt.trans_no WHERE cot.offer_user_no IS NULL
AND cot.offer_st_cd = #{offerStatusCd}
) offer ORDER BY
${sortField} ${sortOrder},
offer.offerRegDate DESC LIMIT ${startIndex}, ${pageSize}

mapper.update:
UPDATE CHALLENGE_OFFER_TB
SET offer_st_cd = 'VO03'
WHERE offer_no = #{offerNo}
