mapper.select:
/* selectPopupList */ SELECT popup_no popupNo , popup_image_url popupImgUrl , popup_tp_cd popupTypeCd, CASE WHEN popup_tp_cd = 'PO01' THEN (SELECT tt.trans_en FROM NOTICE_TB nt INNER JOIN TRANSLATION_TB tt ON nt.notice_title_t_no = tt.trans_no WHERE nt.notice_no = pt.popup_target_no) ELSE (SELECT tt.trans_en FROM OPERATED_CHALLENGE_TB oct INNER JOIN BASE_CHALLENGE_TB bct ON oct.challenge_base_no = bct.base_no INNER JOIN TRANSLATION_TB tt ON bct.base_nm_t_no = tt.trans_no WHERE oct.challenge_no = pt.popup_target_no) END popupTargetNameEn , popup_is_on popupIsOn FROM POPUP_TB pt ORDER BY
${sortField} ${sortOrder},
popup_is_on DESC, popup_no DESC LIMIT ${startIndex}, ${pageSize}

/* getTotalNoticeCount */
SELECT COUNT(*) totalNoticeCount
FROM NOTICE_TB

/* selectNoticeList */
SELECT nt.notice_no contentNo, tt.trans_en contentTitleEn, DATE_FORMAT(nt.notice_reg_dt, '%Y-%m-%d %H:%i:%s') contentRegDate
FROM NOTICE_TB nt INNER JOIN TRANSLATION_TB tt ON nt.notice_title_t_no = tt.trans_no
LIMIT ${startIndex}, ${pageSize}

/* selectChallengeList */
SELECT oct.CHALLENGE_NO contentNo, tt.TRANS_EN contentTitleEn, date_format(oct.CHALLENGE_REG_DT, '%Y-%m-%d %H:%i:%s') contentRegDate
FROM OPERATED_CHALLENGE_TB oct inner join BASE_CHALLENGE_TB bct on oct.CHALLENGE_BASE_NO = bct.BASE_NO
INNER join TRANSLATION_TB tt on bct.BASE_NM_T_NO = tt.TRANS_NO
WHERE oct.CHALLENGE_ST_CD = 'CH01'
LIMIT ${startIndex}, ${pageSize}

mapper.insert:
/* insertNewPopup */
INSERT INTO POPUP_TB (popup_image_url, popup_tp_cd, popup_target_no, popup_is_on)
VALUES (#{popupImgUrl}, #{popupTypeCd}, #{popupTargetNo}, 0)

mapper.update:
/* deactivatePopups */
UPDATE POPUP_TB 
SET popup_is_on = 0 
WHERE popup_no in 

	#{popup}
