mapper.select:
/* getTotalUserCount */
SELECT COUNT(*) totalUserCount
FROM USER_TB 
WHERE user_nationality_cd = #{nationalityCd}

SELECT user_no userNo, user_id userId, user_nationality_cd nationalityCd, user_nickname nickName, user_login_tp_cd loginTypeCd, DATE_FORMAT(user_reg_dt, '%Y-%m-%d %h:%i:%s') userRegDate FROM USER_TB WHERE ( user_id LIKE CONCAT('%', #{userSearchText}, '%') OR user_nickname LIKE CONCAT('%', #{userSearchText}, '%') ) AND user_nationality_cd = #{nationalityCd} ORDER BY
${sortField} ${sortOrder},
user_no DESC LIMIT ${startIndex}, ${pageSize}
