mapper.select:
/* getTotalUserCount */
SELECT COUNT(*) totalUserCount
FROM USER_TB 
WHERE (user_id LIKE CONCAT('%', #{userSearchText}, '%')
OR user_nickname LIKE CONCAT('%', #{userSearchText}, '%') ) 
AND user_nationality_cd = #{nationalityCd}

/* selectUserListByIdAndNickname */ SELECT user_no userNo, user_id userId, user_nationality_cd nationalityCd, user_nickname nickName, user_cash userCash , IFNULL((SELECT SUM(report_cash_amt) FROM USER_CASH_REPORT_TB WHERE report_tp_cd = 'CA03' AND report_st_cd = 'CT02' AND cash_user_no = ut.user_no), 0) withdrawAmount , IFNULL((SELECT SUM(report_cash_amt) FROM USER_CASH_REPORT_TB WHERE report_tp_cd = 'CA02' AND report_st_cd = 'CT02' AND cash_user_no = ut.user_no), 0) rewardAmount , y_card_num yellowCardNumber, r_card_num redCardNumber, IFNULL(r_card_expired_dt, '-') redCardExpiredDate FROM USER_TB ut WHERE (user_id LIKE CONCAT('%', #{userSearchText}, '%') OR user_nickname LIKE CONCAT('%', #{userSearchText}, '%')) AND user_nationality_cd = #{nationalityCd} ORDER BY
${sortField} ${sortOrder},
user_no DESC LIMIT ${startIndex}, ${pageSize}

/* selectUserDetail */
SELECT user_no userNo, user_id userId, user_grade_cd userGrade, CONCAT(user_first_nm, ' ', user_last_nm) username, user_nickname nickname, user_phonenum phoneNumber, user_email email, user_image_url userImageUrl, user_cash userCash, y_card_num yellowCardNumber, r_card_num redCardNumber, IFNULL(r_card_expired_dt, '-') redCardExpiredDate 
FROM USER_TB 
WHERE user_no = #{userNo}

mapper.update:
/* increaseYellowCardNumber */
UPDATE USER_TB
SET y_card_num = y_card_num + 1
WHERE user_no = #{userNo}

/* increaseRedCardNumber */
UPDATE USER_TB
SET y_card_num = 0, r_card_num = r_card_num + 1
WHERE user_no = #{userNo}

/* updateRedCardExpiredDate */
UPDATE USER_TB
SET R_CARD_EXPIRED_DT = DATE_ADD(NOW(), INTERVAL ${expiredMonthNumber} MONTH)
WHERE user_no = #{userNo}

/* clearToken */
UPDATE USER_TB
SET user_login_token = 'Red Card'
WHERE user_no = #{userNo}

mapper.select:
/* selectCashReportList */
SELECT cash_report_no cashReportNo, report_cash_amt reportCashAmt, DATE_FORMAT(report_reg_dt, '%Y-%m-%d %h:%i:%s') cashReportRegDate, report_st_cd reportStatusCd
FROM USER_CASH_REPORT_TB
WHERE cash_user_no = #{userNo}
AND report_st_cd != 'CT03'
AND report_tp_cd = #{cashReportType}
ORDER BY report_reg_dt DESC

mapper.update:
/* subtractReward */
UPDATE USER_TB
SET user_cash = (
	CASE
		WHEN user_cash > (SELECT IFNULL(SUM(report_cash_amt), 0) FROM USER_CASH_REPORT_TB WHERE cash_user_no = #{userNo} AND report_tp_cd = 'CA02' AND report_st_cd='CT02')  THEN user_cash - (SELECT IFNULL(SUM(report_cash_amt), 0) FROM USER_CASH_REPORT_TB WHERE cash_user_no = #{userNo} AND report_tp_cd = 'CA02' AND report_st_cd='CT02')
        ELSE 0
	END
)
WHERE USER_NO = #{userNo}

mapper.select:
/* selectTotalReward */
SELECT IFNULL(SUM(report_cash_amt), 0) 
FROM USER_CASH_REPORT_TB 
WHERE cash_user_no = #{userNo}
AND report_tp_cd = 'CA02' 
AND report_st_cd='CT02'
